import React, {Component} from 'react';
import {Route, Link} from 'react-router-dom';
import NoteListNav from '../NoteListNav/NoteListNav';
import NotePageNav from '../NotePageNav/NotePageNav';
import NoteListMain from '../NoteListMain/NoteListMain';
import NotePageMain from '../NotePageMain/NotePageMain';
import AddFolder from '../AddFolder/AddFolder';
import AddNote from '../AddNote/AddNote';
import ApiContext from '../ApiContext';
import config from '../config';
import NotefulError from '../NotefulError';
import './App.css';
import { throws } from 'assert';

export default class App extends Component {
    state = {
        notes: [],
        folders: [],
        folderName: '',
        noteNameInput: '',
        noteContentInput:'',
        noteFolderSelect: '',
        noteNameInputVaild: false,
        formValid: false,
        validationMessages: {
        noteNameInput: ''
        }
    }

    addNewFolder = folderName => {
        this.setState({
            folderName
        })
    }


    validateNoteNameInputVaild(fieldValue) {
        const fieldErrors = {...this.state.validationMessages};
        let hasError = false;

        fieldValue = fieldValue.trim();
        if(fieldValue.length === 0) {
          fieldErrors.noteNameInput = 'A name is required for the note';
          hasError = true;
        } else {
          fieldErrors.noteNameInput= '';
          hasError = false;
          }
        this.setState({
          validationMessages: fieldErrors,
          noteNameInputVaild: !hasError
        }, this.formValid );
      }
        formValid() {
          this.setState({
            formValid: this.state.noteNameInputValid
          });
        }


    addNewNote = noteNameInput => {
        console.log('addNewNote ran')
        this.setState({
            noteNameInput}, () => 
            {this.validateNoteNameInputVaild(noteNameInput)});
      }

      addNoteContent = noteContentInput => {
        this.setState({
            noteContentInput
        })
      }

      selectFolder = noteFolderSelect => {
        this.setState({
            noteFolderSelect
        })
      }


    componentDidMount() {
        Promise.all([
            fetch(`${config.API_ENDPOINT}/notes`),
            fetch(`${config.API_ENDPOINT}/folders`)
        ])
            .then(([notesRes, foldersRes]) => {
                if (!notesRes.ok)
                    return notesRes.json().then(e => Promise.reject(e));
                if (!foldersRes.ok)
                    return foldersRes.json().then(e => Promise.reject(e));

                return Promise.all([notesRes.json(), foldersRes.json()]);
            })
            .then(([notes, folders]) => {
                this.setState({notes, folders});
            })
            .catch(error => {
                console.error({error});
            });
    }

    handleDeleteNote = noteId => {
        this.setState({
            notes: this.state.notes.filter(note => note.id !== noteId)
        });
    };

    renderNavRoutes() {
        return (
            <>
                {['/', '/folder/:folderId'].map(path => (
                    <Route
                        exact
                        key={path}
                        path={path}
                        component={NoteListNav}
                    />
                ))}
                <Route path="/note/:noteId" component={NotePageNav} />
                <Route path="/add-folder" component={NotePageNav} />
                <Route path="/add-note" component={NotePageNav} />
            </>
        );
    }

    renderMainRoutes() {
        return (
            <>
                {['/', '/folder/:folderId'].map(path => (
                    <Route
                        exact
                        key={path}
                        path={path}
                        component={NoteListMain}
                    />
                ))}
                <Route path="/note/:noteId" component={NotePageMain} />

                <Route path='/add-folder' 
                    render={() =>
                    <AddFolder
                        folderName={this.folderName}
                        addNewFolder={this.addNewFolder}
                    />}
                />
               <Route path='/add-note'
                render={() =>
                    <AddNote
                    noteNameInput = {this.noteNameInput}
                    noteContentInput = {this.noteContentInput}
                    noteFolderSelect = {this.noteFolderSelect}
                    noteNameInputVaild = {this.noteNameInputVaild}
                    formVaild = {this.formValid}
                    validationMessages = {this.validationMessages} 
                    addNewNote = {this.addNewNote }
                    addNoteContent = {this.addNoteContent}
                    selectFolder = {this.selectFolder}

                    /> }
                />
            </>
        );
    }

    render() {
        const value = {
            notes: this.state.notes,
            folders: this.state.folders,
            deleteNote: this.handleDeleteNote
        };
        return (
            <ApiContext.Provider value={value}>
                <div className="App">
                    <nav className="App__nav">{this.renderNavRoutes()}</nav>
                    <header className="App__header">
                        <h1>
                            <Link to="/">Noteful</Link>{' '}
                        </h1>
                    </header>
                    <main className="App__main">{this.renderMainRoutes()}</main>
                </div>
            </ApiContext.Provider>
        );
    }
}
