import React, {Component} from 'react';
import {Route, Link} from 'react-router-dom';
import {withRouter} from 'react-router-dom';
import NoteListNav from '../NoteListNav/NoteListNav';
import NotePageNav from '../NotePageNav/NotePageNav';
import NoteListMain from '../NoteListMain/NoteListMain';
import NotePageMain from '../NotePageMain/NotePageMain';
import AddFolder from '../AddFolder/AddFolder';
import AddNote from '../AddNote/AddNote';
import EditNote from '../EditNote/EditNote';
import EditFolder from'../EditFolder/EditFolder';
import ApiContext from '../ApiContext';
import config from '../config';
import './App.css'

class App extends Component {

    state = {
        folders: [],
      }

       foldersRes = [
        { id: 1, name: 'Folder 1' },
        { id: 2, name: 'Folder 2' },
      ]

     notesRes = [
        { id: 1, name: 'Donkey Rhubarb 1', folderId: 1 },
        { id: 2, name: 'Donkey Rhubarb 2', folderId: 2 },
        { id: 3, name: 'Donkey Rhubarb 3', folderId: 2 },
        { id: 4, name: 'Donkey Rhubarb 4', folderId: 1 },
        { id: 5, name: 'Donkey Rhubarb 5', folderId: 2 },
      ]


      addFolderContent = () => {

      state.folders = notesRes.reduce((foldersRes, note) => {
        const index = foldersRes.findIndex(f => f.id === note.folderId) 
        if (!foldersRes[index].notes) 
          foldersRes[index].notes = []
        foldersRes[index].notes.push({ 
          id: note.id, 
          name: note.name 
        })
        return foldersRes
      }, foldersRes)

      console.log(JSON.stringify(state, null, 3))
    }

    // state = {
    //     notes: [],
    //     folders: []  
    // };

    addNewFolder = folder => {
        this.setState({ 
            folders: [ ...this.state.folders, folder 
        ]})
    }

    addNewNote = (note) => {
        this.setState({ 
            notes: [ ...this.state.notes, note
        ]});
    };

    editNote= editedNote => {
        const newNotes = this.state.notes.map(note =>
            (note.id === editedNote.id)
            ? editedNote
            : note
            )
        this.setState({
            notes: newNotes
        })
    }

    componentDidMount() {
        Promise.all([
            fetch(`${config.API_ENDPOINT}/api/notes`),
            fetch(`${config.API_ENDPOINT}/api/folders`)
        ])
            .then(([notesRes, foldersRes]) => {

                if (!notesRes.ok)
                    return notesRes.json().then(e => Promise.reject(e));
                if (!foldersRes.ok)
                    return foldersRes.json().then(e => Promise.reject(e));

                    return Promise.all([notesRes.json(), foldersRes.json()]);
            })
            .then(([notes, folders]) => {
                console.log(notes, folders)
                this.setState({notes, folders});
            })
            .catch(error => {
                console.error({error});
            });
    };

    handleDeleteNote = noteId => {
        this.setState({
            notes: this.state.notes.filter(note => note.id !== noteId)
        });
        this.props.history.push(`/`)
    };

    handleDeleteFolder = folderId => {
        this.setState({
            folders: this.state.folders.filter(folder => folder.id !== folderId)
        });
    };

    renderNavRoutes() {
        return (
            <>
                {['/', '/folder/:folderId'].map(path => (
                    <Route
                        exact
                        key={path}
                        path={path}
                        component={NoteListNav}
                    />
                ))}
                <Route path="/note/:noteId" component={NotePageNav} />
                <Route path="/add-folder" component={NotePageNav} />
                <Route path="/add-note" component={NotePageNav} />
            </>
        );
    }

    renderMainRoutes() {
        return (
            <>
                {['/', '/folder/:folderId'].map(path => (
                    <Route
                        exact
                        key={path}
                        path={path}
                        component={NoteListMain}
                    />
                ))}
                <Route 
                    path="/note/:noteId" 
                    component={NotePageMain} 
                />

                <Route path='/add-folder' 
                    render={(history) =>
                <AddFolder
                    addNewFolder={this.addNewFolder}
                />}
                />
               <Route path='/add-note'
                render={(history) =>
                    <AddNote
                        addNewNote = {this.addNewNote}
                        folders ={this.state.folders} 
                    /> }
                />
                <Route path='/edit/:noteId'
                    render={(history) =>
                    <EditNote
                        editNote = {this.EditNote}
                        folders ={this.state.folders} 
                    /> }
                />

                <Route path='/edit/:folderId'
                    render={(history) =>
                    <EditFolder
                        editFolder = {this.EditFolder}
                        folders = {this.state.folders} 
                        notes = {this.state.notes}
                    /> }
            />          
            </>
        );
    }

    render() {
            const value = {
            notes: this.state.notes,
            folders: this.state.folders,
            deleteNote: this.handleDeleteNote,
            deleteFolder: this.handleDeleteFolder
            };

        return (
            <ApiContext.Provider value={value}>
                <div className="App">
                    <nav className="App__nav">{this.renderNavRoutes()}</nav>
                    <header className="App__header">
                        <h1>
                            <Link to="/">Noteful</Link>{' '}
                        </h1>
                    </header>
                    <main className="App__main">{this.renderMainRoutes()}</main>
                </div>
            </ApiContext.Provider>
        );
    }
}

export default withRouter(App)
