import React, { Component } from 'react';
import NotefulForm from '../NotefulForm/NotefulForm';
import './AddNote.css';
import ValidationError from './ValidationError';
import ApiContext from '../ApiContext';
import NotefulError from '../NotefulError'

export default class AddNote extends Component {

  constructor(props) {
    super(props);
    this.state = {
      noteNameInput: '',
      noteContentInput:'',
      noteFolderSelect: '',
        noteNameInputVaild: false,
      formValid: false,
      validationMessages: {
        noteNameInput: ''
      }
    }
  }

  addNewNote(noteNameInput) {
    console.log('addNewNote ran')
    this.setState({noteNameInput})
  }

  addNoteContent(noteContentInput) {
    console.log('addNoteContent ran')
    this.setState({noteContentInput})

    console.log(this.state)
  }

  selectFolder(noteFolderSelect) {
    this.setState({noteFolderSelect})
  }

  handleSubmit = e => {
    console.log('handleSubmit ran')
    e.preventDefault()
    const newNote = this.state.noteNameInput;
    const newContent = this.state.noteContentInput;
    const selectedFolder = this.state.noteFolderSelect;
    const url = 'http://localhost:9090/notes';
    const options = {
      method: 'POST',
      headers: {
        'content-type': 'application/json'
      },
      body: JSON.stringify(
        {
        id: 'd26e11a0-ffaf-11e8-8eb2-f2801f1b9fd1',
        name: newNote,
        folderId: selectedFolder,
        content: newContent
    })
    }

    fetch(url, options)
      .then(res => {
        if(!res.ok) {
          throw new Error('Something went wrong, please try again later');
        }
        return res.json();
      })
    .then(data => {
      this.setState({
        name: '',
        folderId: '',
        content: '',
        folders: []
      });
    })
    .catch(err => {
      this.setState({
        error: err.message
      });
    });
  }

  static contextType = ApiContext;

  validateName(fieldValue) {
    const fieldErrors = {...this.state.validationMessages};
    let hasError = false;

    fieldValue = fieldValue.trim();
    if(fieldValue.length === 0) {
      fieldErrors.noteNameInput = 'A name is required for the note';
      hasError = true;
    } else {
      fieldErrors.noteNameInput= '';
      hasError = false;
      }

    this.setState({
      validationMessages: fieldErrors,
      noteNameInputVaild: !hasError
    }, this.formValid );
  }

  //   formValid() {
  //     this.setState({
  //       formValid: this.state.nameNameInputValid
  //     });
  //   }
  // }

  render() {
    const { folders=[] } = this.context
    return (
      <section className='AddNote'>
        <NotefulError>
          <h2>Create a note</h2>
          <NotefulForm onSubmit={this.handleSubmit}>
            <div className='field'>
              <label htmlFor='note-name-input'>
                Name
              </label>
              <input type='text' id='noteNameInput' name='noteNameInput' onChange={e => this.addNewNote(e.target.value)}/>
              <ValidationError hasError={!this.state.nameValid} message={this.state.validationMessages.name}/>
            </div>
            <div className='field'>
              <label htmlFor='note-content-input'>
                Content
              </label>
              <textarea id='noteContentInput' name='noteContentInput' onChange={e => this.addNoteContent(e.target.value)}/>
            </div>
            <div className='field'>
              <label htmlFor='note-folder-select'>
                Folder
              </label>
              <select id='noteFolderSelect' name='noteFolderSelect' onChange={e => this.selectFolder(e.target.value)}>
                <option value={null}>...</option>
                {folders.map(folder =>
                  <option key={folder.id} value={folder.id}>
                    {folder.name}
                  </option>
                )}
              </select>
            </div>
            <div className='buttons'>
              <button type='submit'>
                Add note
              </button>
            </div>
          </NotefulForm>
        </NotefulError>
      </section>
    )
  }
}
