import React, { Component } from 'react';
import './EditFolder.css'

export default class EditFolder extends Component {

    // constructor (props) {
    //    super(props) 
    //    this.stat
    // }

    state = {
        name: null
    }

    componentDidMount() {

    const folderId = this.props.match.params.folderId

    fetch('http://localhost:8000/api/folders/${folderId}', { method: 'GET'})
        .then(res => {
        if(!res.ok) {
            throw new Error('Something went wrong, please try again later');
        }
        return res.json();
        })  
    .then(data => {
        this.setState({
                name: data.name
        })
        })
    .catch(err => {
        this.setState({
            error: err.message
            });
        })
    }

    handleSubmit = e => {
        e.preventDefault()

        fetch(
            'http://localhost:8000/api/folders/${this.props.match.params.folderId}', {
             method: 'PATCH',
            body: JSON.stringify(this.state.inputValues)
        })
        .then(res => {
        if(!res.ok) {
            throw new Error('Something went wrong, please try again later');
        }
        return res.json();
        })  
    .then(data => {
        this.context.updateFolder(data)
     })
     .catch(err => {
        this.setState({
            error: err.message
            });
        })
}

render() {
    const { name } = this.state

    return (
      <section className='EditFolder'>
        <h2>Edit Folder</h2>
        <form onSubmit={this.handleSubmit}> 
            <div className='field'>
                <label htmlFor='name-input'>
                Name:
                </label>
                <input 
                    type='text' 
                    id='name' 
                    name='name'
                    value={name}
                    onChange={this.handleNameChange}
                    />
            </div>
            <div className='buttons'>
                <button className='editButton' type='submit'>
                Edit folder
                </button>
            </div>
        </form>
      </section>
    )
  }
}
