import React from 'react'

const ApiContext =  React.createContext({

  render() {
    const contextValue = {
    folders: this.state.folders,
    notes: [],
    addFolder: () => {},
    addNote: () => {},
    deleteNote: () => {},
    updateNote: () => {},
    updateFolder: this.updateFolder,
    deleteFolder: () => {}
    }
  }

})

export default ApiContext
