import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import NotefulForm from '../NotefulForm/NotefulForm';
import './AddNote.css';
import ValidationError from './ValidationError';
import NotefulError from '../NotefulError';
import ApiContext from '../ApiContext';
import PropTypes from 'prop-types'

class AddNote extends Component {

  static defaultProps = {
    addNewNote: () => {}
  };

constructor(props) {
  super(props);
  this.state = {
    error: null,
    name: '',
        nameValid: false,
        formValid: false,
        validationMessages: {
          name: ''
        }
  };
}

verifyNameLength(name) {
  this.setState({name},() => {this.validateName(name)});
}

validateName(fieldValue) {
  const fieldErrors = {...this.state.validationMessages};
  let hasError = false;
  fieldValue = fieldValue.trim();
  if(fieldValue.length === 0) {
    fieldErrors.name = 'A note name is required';
    hasError = true;
    } else {
      fieldErrors.name = '';
      hasError = false;
    }
  }

formValid() {
  this.setState({
      formValid: this.state.nameValid
  });
}

  handleNoteSubmit = e => {
    e.preventDefault()
    const { name, selectedFolder, content } = e.target
    const note = {
      name: name.value,
      folderId: selectedFolder.value,
      content: content.value,
      modified: Date.now()
    }
    const url = 'http://localhost:9090/notes';
    const options = {
      method: 'POST',
      headers: {
        'content-type': 'application/json'
      },
      body: JSON.stringify(note)
    }
    this.setState({ error: null })
    fetch(url, options)
      .then(res => {
        if(!res.ok) {
          throw new Error('Something went wrong, please try again later');
        }
        return res.json();
      })
    .then(data => {
      this.props.addNewNote(data)
      this.props.history.push('/')
    })
    .catch(err => {
      this.setState({
        error: err.message
      });
    });
  }

  render() {
    const { error } = this.state
    const folders = this.props.folders
    return (
      <section className='AddNote'>
        <NotefulError>
          <h2>Create a note</h2>
          <NotefulForm onSubmit={this.handleNoteSubmit}>
            <div className='field'>
              <label htmlFor='note-name-input'>
                Name
              </label>
              <input type='text' id='name' name='name' onChange={e => this.verifyNameLength(e.target.value)}/>
              <ValidationError hasError={!this.state.nameVaild} message={this.state.validationMessages.name}/>
            </div>
            <div className='field'>
              <label htmlFor='note-content-input'>
                Content
              </label>
              <textarea id='content' name='content'/>
            </div>
            <div className='field'>
              <label htmlFor='note-folder-select'>
                Folder
              </label>
              <select id='selectedFolder' name='selectedFolder'>
                <option value={null}>...</option>
                {folders.map(folder =>
                  <option key={folder.id} value={folder.id}>
                    {folder.name}
                  </option>
                )}
              </select>
            </div>
            <div className='buttons'>
              <button type='submit' disabled={!this.state.formValid} >
                Add note
              </button>
            </div>
          </NotefulForm>
        </NotefulError>
      </section>
    )
  }
}

AddNote.propTypes = {
  addNewNote: PropTypes.func.isRequired,
  folders: PropTypes.arrayOf(PropTypes.shape({
    name: PropTypes.string.isRequired
  })) 
};

export default withRouter(AddNote);
