import React, { Component } from 'react';
import NotefulForm from '../NotefulForm/NotefulForm';
import './AddNote.css';
import { format } from 'date-fns';
import ValidationError from './ValidationError';
import ApiContext from '../ApiContext';
import NotefulError from '../NotefulError'
import PropTypes from 'prop-types';

export default class AddNote extends Component {

  static contextType = ApiContext;

  render() {
    const { folders=[] } = this.context
    return (
      <section className='AddNote'>
        <NotefulError>
          <h2>Create a note</h2>
          <NotefulForm onSubmit={this.handleSubmit}>
            <div className='field'>
              <label htmlFor='note-name-input'>
                Name
              </label>
              <input type='text' id='noteNameInput' name='noteNameInput' onChange={e => this.props.addNewNote(e.target.value)}/>
              <ValidationError hasError={!this.state.noteNameInputVaild} message={this.state.validationMessages.noteNameInput}/>
            </div>
            <div className='field'>
              <label htmlFor='note-content-input'>
                Content
              </label>
              <textarea id='noteContentInput' name='noteContentInput' onChange={e => this.props.addNoteContent(e.target.value)}/>
            </div>
            <div className='field'>
              <label htmlFor='note-folder-select'>
                Folder
              </label>
              <select id='noteFolderSelect' name='noteFolderSelect' onChange={e => this.props.selectFolder(e.target.value)}>
                <option value={null}>...</option>
                {folders.map(folder =>
                  <option key={folder.id} value={folder.id}>
                    {folder.name}
                  </option>
                )}
              </select>
            </div>
            <div className='buttons'>
              <button type='submit'>
                Add note
              </button>
            </div>
          </NotefulForm>
        </NotefulError>
      </section>
    )
  }

  // AddNote.propTypes = {
  //   noteNameInput: PropTypes.string.isRequired
  // };

}

