import React, { Component } from  'react';
import { withRouter } from 'react-router-dom'
import PropTypes from 'prop-types';
import NotefulForm from '../NotefulForm/NotefulForm';
import NotefulError from '../NotefulError';
import config from '../config'
import './EditFolder.css';

class EditFolder extends Component {

    state = {
        error: null,
      };

      componentDidMount() {
        const folderId = this.props.match.params.folderId
       fetch(`https://localhost:8000/api/folders/${folderId}`, {
           method: 'GET'
        })
        .then(res => {
         if(!res.ok) {
           throw new Error('Something went wrong, please try again later');
         }
         return res.json();
       })
       .then(responseData => {
              this.setState({
               /* fields state updates here? */
             })
            })
            .catch(error => {
               console.error({error});})
     }

    handleFolderEdit = e => {
        e.preventDefault()
        const {name} = e.target
        const folder = {
          name: name.value
        }
        fetch(`https://localhost:8000/api/folders/${this.props.match.params.folderId}`, {
           method: 'PATCH',
           body: JSON.stringify(this.state.inputValue)
         })
           .then(res => {
            if(!res.ok) {
              throw new Error('Something went wrong, please try again later');
            }
            return res.json();
           })
            .then(data => {
            this.props.EditFolder(data)
            this.props.history.push('/')
            })
            .catch(err => {
            this.setState({
                error: err.message
            });
            });
    }

    render() {
        return (
          <section className='EditFolder'>
            <NotefulError>
            <h2>Edit folder</h2>
            <NotefulForm onSubmit={this.handleFolderEdit}>
              <div className='field'>
                <label htmlFor='name-input'>
                  Name
                </label>
                <input type='text' id='name' name='name'/>
              </div>
              <div className='buttons'>
                <button type='submit'>
                  Edit folder
                </button>
              </div>
            </NotefulForm>
            </NotefulError>
          </section>
        )
      }
    }

export default EditFolder;
