import React, { Component } from 'react';
import NotefulForm from '../NotefulForm/NotefulForm';
import './AddNote.css';
import { format } from 'date-fns';
import ValidationError from './ValidationError';
import ApiContext from '../ApiContext';
import NotefulError from '../NotefulError'
import PropTypes from 'prop-types';
import { withRouter } from 'react-router-dom';

class AddNote extends Component {

  static defaultProps = {
    addNewNote: () => {}
  };

  state = {
    error: null,
    name: '',
    selectedFolder: '',
    content: '',
    nameValid: false,
    validationMessages: {
      name: '',
    }
  };

  handleNoteSubmit = e => {
    e.preventDefault()
    const { name, selectedFolder, content } = e.target
    const note = {
      name: name.value,
      selectedFolder: selectedFolder.value,
      content: content.value
    }
    const url = 'http://localhost:9090/notes';
    const options = {
      method: 'POST',
      headers: {
        'content-type': 'application/json'
      },
      body: JSON.stringify(note)
    }
    this.setState({ error: null })
    fetch(url, options)
      .then(res => {
        if(!res.ok) {
          throw new Error('Something went wrong, please try again later');
        }
        return res.json();
      })
    .then(data => {
      name.value = ''
      selectedFolder.value = '' 
      content.value = ''
      this.props.addNewNote(data)
      this.props.history.push('/')
    })
    .catch(err => {
      this.setState({
        error: err.message
      });
    });
  }


  render() {
    const { error } = this.state
    const { folders=[] } = this.context
    return (
      <section className='AddNote'>
        <NotefulError>
          <h2>Create a note</h2>
          <NotefulForm onSubmit={this.handleNoteSubmit}>
            <div className='field'>
              <label htmlFor='note-name-input'>
                Name
              </label>
              <input type='text' id='name' name='name'/>
              <ValidationError hasError={!this.props.noteNameInputVaild} message={this.props.validationMessages.noteNameInput}/>
            </div>
            <div className='field'>
              <label htmlFor='note-content-input'>
                Content
              </label>
              <textarea id='content' name='content'/>
            </div>
            <div className='field'>
              <label htmlFor='note-folder-select'>
                Folder
              </label>
              <select id='selectedFolder' name='selectedFolder'>
                <option value={null}>...</option>
                {folders.map(folder =>
                  <option key={folder.id} value={folder.id}>
                    {folder.name}
                  </option>
                )}
              </select>
            </div>
            <div className='buttons'>
              <button type='submit'>
                Add note
              </button>
            </div>
          </NotefulForm>
        </NotefulError>
      </section>
    )
  }

  // AddNote.propTypes = {
  //   noteNameInput: PropTypes.string.isRequired
  // };

}

// disabled={!this.state.formValid}>


export default withRouter(AddNote);
