import React, { Component } from  'react';

export default class EditNote extends Component {
    componentDidMount() {
        fetch(`http://localhost:8000/api/note/:noteId`, {
            method: 'GET'
        })
        .then(response => response.json())
        .then(data => {
            /* fields state updates here */
            })
}

  render() {
    return (
      <section className='EditNoteForm'>
        <h2>Edit Note</h2>
        <form>
        <div className='field'>
              <label htmlFor='note-name-input'>
                Name
              </label>
              <input type='text' id='name' name='name' onChange={e => this.verifyNameLength(e.target.value)}/>
            </div>
            <div className='field'>
              <label htmlFor='note-content-input'>
                Content
              </label>
              <textarea type='text' id='content' name='content'/>
            </div>
            <div className='field'>
              <label htmlFor='note-folder-select'>
                Folder
              </label>
              <select id='selectedFolder' name='selectedFolder'>
                <option value={null}>...</option>
                {/* {folders.map(folder =>
                  <option key={folder.id} value={folder.id}>
                    {folder.name}
                  </option> */}
                )}
              </select>
            </div>
            <div className='buttons'>
              <button type='submit' disabled={!this.state.formValid} >
                Add note
              </button>
            </div>
        </form>
      </section>
    )
  }
}
